sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var what_needed = g_form.getvalue('u_what_needed');
	//clearing the request type options
	g_form.clearOptions('u_what_needed');
	//if the value of request type is human resource,add humana resource1&human resource 2&other

	if(newValue == 'Human Resources')
		{
	g_form.addOption('u_what_needed','Human Resources','Human Resources1');
	g_form.addOption('u_what_needed','Human Resources','Human Resources2');
	g_form.addOption('u_what_needed','Human Resources','other');
		}
	//if the value of request type is facilitiesfacilities1&facilities2&other
	if(newValue == 'Facilities')
		{
	g_form.addOption('u_what_needed','Facilities','Facilities1');
	g_form.addOption('u_what_needed','Facilities','Facilities2');
	g_form.addOption('u_what_needed','Facilities','other');
		}
//if the request type value is legal,add lega1,legal2,other
	if(newValue == 'Legal')
		{
	g_form.addOption('u_what_needed','Legal','Legal1');
	g_form.addOption('u_what_needed','Legal','Legal2');
	g_form.addOption('u_what_needed','Legal','other');   
		}


			// If the form is loading and it is not a new record, set the u_what_needed value to the
		// value from the record before it was loaded

			if(isLoading & !g_form.isNewRecord())
				{
					g_form.setValue('u_what_needed','whatneeded');
				}

}
